import React, { Component, useContext, useEffect, useState } from "react";
import NewArticle from "./newArticle";
import ArticleThumbnail from "./articleThumbnail";
import { Button } from "reactstrap";
import { ArticleContext } from "../context/ArticleContext";

const Main = () => {
  const [monzacState, setMonzacState] = useContext(ArticleContext);
  const [enableCreateNew, setEnableCreateNew] = useState(0);

  useEffect(() => {
    getCategoryList();
  });
  const getCategoryList = () => {
    fetch("/get/category")
      .then((res) => res.json())
      .then((res) => {
        let catList = res.map((r) => r.name);
        console.log(catList);
        monzacState[2] = catList;
      });
  };
  const getArticleThumbList = () => {
    fetch("/api/thumbist")
      .then((res) => res.json())
      .then((res) => {
        let articleThumbList = res.map(
          (t) => t.title,
          (t) => t.content
        );
        this.setState({ articleThumbList });
      });
  };
  const enableNewArticleComponent = () => {
    //TODO use setMonzacState
    monzacState[4] = !monzacState[4];
    setEnableCreateNew(monzacState[4]);
    console.log(monzacState);
  };

  return (
    <React.Fragment>
      <div align="right" style={{ padding: 3 }}>
        <Button outline color="info" onClick={enableNewArticleComponent}>
          Write an article
        </Button>
      </div>
      {enableCreateNew ? <NewArticle></NewArticle> : null}
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
    </React.Fragment>
  );
};

export default Main;
