  //   value: "",
  //   id: "",
  // },
  // {
  //   name: "catList",
  //   value: [],
  // },
  // {
  //   name: "articleThumbList",
  //   value: [],
  // },
  // {
  //   name: "isCreateNewArticleEnabled",
  //   value: false,
  // },
  // ]);

  return (
    <ArticleContext.Provider value={{ monzacState, setMonzacState }}>
      {props.children}
    </ArticleContext.Provider>
  );
};
