// new article creation
import React, { Component } from "react";
import { Row, Col, Input } from "reactstrap";

class CreateNewArticle extends Component {
  state = {};
  styles = {
    boxShadow: "none",
    boder: 0
  };
  render() {
    return (
      <Row className="row">
        <Col>
          <Input placeholder="Article Title"></Input>
        </Col>
        <Col xs="6">
          <Input placeholder="Category..."></Input>
        </Col>
        <Col xs="6"></Col>
        <Col xs="12" style={{ padding: "2" }}>
          <Input style={this.styles} placeholder="Content..."></Input>
        </Col>
      </Row>
    );
  }
}

export default CreateNewArticle;
