import React, { Component, useContext } from "react";
import NewArticle from "./newArticle";
import ArticleThumbnail from "./articleThumbnail";
import { Button } from "reactstrap";
import { ArticleContext } from "../context/ArticleContext";

//class Main extends Component {
const Main = () => {
  const { monzacState, setMonzacState } = useContext(ArticleContext);
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     isCreateNewArticleEnabled: false,
  //     articleThumbList: {
  //       title: "",
  //       content: "",
  //     },
  //   };
  //   this.enableNewArticleComponent = this.enableNewArticleComponent.bind(this);
  // }
  const getArticleThumbList = () => {
    fetch("/api/thumbist")
      .then((res) => res.json())
      .then((res) => {
        let articleThumbList = res.map(
          (t) => t.title,
          (t) => t.content
        );
        this.setState({ articleThumbList });
      });
  };
  const enableNewArticleComponent = () => {
    //TODO use setMonzacState
    monzacState.forEach((element, index) => {
      if (element.name === "isCreateNewArticleEnabled")
        monzacState[index].value = !monzacState[index].value;
    });
  };

  // render() {
  return (
    <React.Fragment>
      <div align="right" style={{ padding: 3 }}>
        <Button outline color="info" onClick={enableNewArticleComponent}>
          Write an article
        </Button>
      </div>
      {this.state.isCreateNewArticleEnabled ? <NewArticle></NewArticle> : null}
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
      <ArticleThumbnail></ArticleThumbnail>
    </React.Fragment>
  );
};
//}

export default Main;
