// new article creation
import React, { Component } from "react";
import { Row, Col, Input } from "reactstrap";
import ArticleEditor from "./articleEditor";
const colStyle = {
  paddingTop: 2,
  paddingLeft: 3,
  paddingRight: 3
};
class CreateNewArticle extends Component {
  state = {};
  styles = {
    boxShadow: "none"
  };
  render() {
    return (
      <Row className="row">
        <Col style={colStyle}>
          <Input style={this.styles} placeholder="Title"></Input>
        </Col>
        <Col xs="6" style={colStyle}>
          <Input style={this.styles} placeholder="Category"></Input>
        </Col>
        <Col xs="6" style={colStyle}></Col>
        <Col xs="12" style={colStyle}>
          <ArticleEditor></ArticleEditor>
        </Col>
      </Row>
    );
  }
}

export default CreateNewArticle;
