// new article creation
import React, { Component, useContext, useEffect } from "react";
import { Row, Col, Input, Button } from "reactstrap";
import ArticleEditor from "./articleEditor";
import { ArticleContext } from "../context/ArticleContext";

const colStyle = {
  paddingTop: 2,
  paddingLeft: 3,
  paddingRight: 3,
};
//const { monzacState, setMonzacState } = useContext(ArticleContext);

//class NewArticle extends Component {

const NewArticle = () => {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     catList: [],
  //   };
  // }
  const [monzacState, setMonzacState] = useContext(ArticleContext);
  // const value = useContext(ArticleContext);

  const styles = {
    boxShadow: "none",
  };
  useEffect(() => {
    getCategoryList();
  });
  updateState((catList) => {
    monzacState.forEach((element, index) => {
      if (element.name === "catList") {
        monzacState[index].value = catList;
      }
    });
  });
  const getCategoryList = () => {
    fetch("/get/category")
      .then((res) => res.json())
      .then((res) => {
        let catList = res.map((r) => r.name);
        setMonzacState((item) => [
          ...item,
          { name: "catList", value: catList },
        ]);
        console.log(monzacState);
      });
  };

  const publishArticleRequest = (e) => {
    e.preventDefault();
    let requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: "React Hooks POST Request Example",
        category: "",
        content: "",
      }),
    };

    fetch("/api/article/create", requestOptions)
      .then((res) => res.json())
      .then((res) => {});
  };

  //render() {

  return (
    <Row className="row">
      <Col xs="6" style={colStyle}>
        <Input style={styles} placeholder="Title"></Input>
      </Col>
      <Col xs="6" style={colStyle}>
        <Input type="select" style={styles}>
          {/* {monzacState.catList.map((opt, i) => (
            <option key={i}>{opt}</option>
          ))} */}
        </Input>
      </Col>
      <Col xs="12" style={colStyle}>
        <ArticleEditor></ArticleEditor>
        <div align="right" style={{ marginBottom: 5 }}>
          <Button outline color="secondary">
            Discard
          </Button>{" "}
          <Button outline color="primary" onClick={publishArticleRequest}>
            Post
          </Button>
        </div>
      </Col>
    </Row>
  );
  // }
};

export default NewArticle;
