import React, { Component } from "react";
import NewArticle from "./newArticle";
import ArticleThumbnail from "./articleThumbnail";
import { Button } from "reactstrap";

class Main extends Component {
  constructor() {
    super();
    this.isCreateNewArticleEnabled = this.isCreateNewArticleEnabled.bind(this);
    this.state = { isCreateNewArticleEnabled: false };
  }
  // state = {
  //   isCreateNewArticleEnabled: false
  // };
  enableNewArticleComponent() {
    this.setState(state => ({
      isCreateNewArticleEnabled: !state.isCreateNewArticleEnabled
    }));
  }

  render() {
    let newart;
    if (this.isCreateNewArticleEnabled) {
      newart = <NewArticle></NewArticle>;
    }

    return (
      <React.Fragment>
        <div align="right" style={{ padding: 3 }}>
          <Button outline color="info" onClick={this.enableNewArticleComponent}>
            Write an article
          </Button>
        </div>
        {newart}
        <ArticleThumbnail></ArticleThumbnail>
        <ArticleThumbnail></ArticleThumbnail>
        <ArticleThumbnail></ArticleThumbnail>
        <ArticleThumbnail></ArticleThumbnail>
        <ArticleThumbnail></ArticleThumbnail>
        <ArticleThumbnail></ArticleThumbnail>
        <ArticleThumbnail></ArticleThumbnail>
      </React.Fragment>
    );
  }
}

export default Main;
