// new article creation
import React, { Component } from "react";
import { Row, Col, Input, Button } from "reactstrap";
import ArticleEditor from "./articleEditor";

const colStyle = {
  paddingTop: 2,
  paddingLeft: 3,
  paddingRight: 3
};

class NewArticle extends Component {
  state = {};
  styles = {
    boxShadow: "none"
  };
  render() {
    return (
      <Row className="row">
        <Col style={colStyle}>
          <Input style={this.styles} placeholder="Title"></Input>
        </Col>
        <Col xs="6" style={colStyle}>
          {/* <Input style={this.styles} placeholder="Category"></Input> */}
          <Input type="select" style={this.styles}>
            <option value="1">Test</option>
          </Input>
        </Col>
        <Col xs="6" style={colStyle}></Col>
        <Col xs="12" style={colStyle}>
          <ArticleEditor></ArticleEditor>
          <div align="right" style={{ marginBottom: 5 }}>
            <Button outline color="secondary">
              Discard
            </Button>{" "}
            <Button outline color="primary">
              Post
            </Button>
          </div>
        </Col>
      </Row>
    );
  }
}

export default NewArticle;
