import React, { Component } from "react";

export const MonzacContext = React.createContext();

export class MonzacProvider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      createArticleTitle: "",
      createArticleDescription: "",
      catList: [],
      articleThumbList: [],
      isCreateNewArticleEnabled: false,
      NewArticleContent: "",
    };
  }

  componentDidMount() {
    fetch("/get/category")
      .then((res) => res.json())
      .then((res) => {
        let catList = res.map((r) => r.name);
        this.state.catList = catList;
      });
  }
  render() {
    return (
      <MonzacContext.Provider
        value={{
          state: this.state,
          toggleIsCreateNewArticleEnabled: () =>
            this.setState((preState) => ({
              isCreateNewArticleEnabled: !preState.isCreateNewArticleEnabled,
            })),
        }}
      >
        {this.props.children}
      </MonzacContext.Provider>
    );
  }
}

